/*global Kakao*/
import axios from 'axios';
import React, {useState, useEffect} from 'react';
import {useHistory} from 'react-router-dom';
import { setToekn, setId } from 'store';
import { useAppContext } from 'store';

import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Alert from 'react-bootstrap/Alert';

import KaKaoLogin from 'react-kakao-login';
import styled from 'styled-components';

const KaKaoBtn = styled(KaKaoLogin)`
  padding: 0;
  width: 300px;
  height: 45px;
  line-height: 44px;
  color: #783c00;
  background-color: #ffeb00;
  border: 1px solid transparent;
  border-radius: 3px;
  font-size: 14px;
  font-weight: bold;
  text-align: center;
  cursor: pointer;
  &:hover {
    box-shadow: 0 0px 15px 0 rgba(0, 0, 0, 0.2);
  }
`;

const SignIn = ({match}) => {
    const {dispatch} = useAppContext();
    const history = useHistory();
    const [show, setShow] = useState(false);
   
    useEffect(() => {
      console.log("###########"+match);
      console.log(match);
      //Kakao.Auth.setAccessToken('m21SQ-BaUZortivUU7WQCT2TrjNQcKtJaUprTLfB4w12DKjMAA8i4X0-8cL2mltsXgk9ugopcBQAAAF2Qn5zTw');
      
      Kakao.API.request({
        url: '/v2/user/me',
        success: function(response) {
            console.log(response);
        },
        fail: function(error) {
            console.log(error);
        }
    });
      
      return () => {
      };
      
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);
    
    const loginWithNaver= async (e) => {
        try {
             const response = await axios.get('allauth/naver/login/');
            console.log(response);
            // 로그인 완료 후 처리
            } catch (e) {
                console.log("###################"+ e);
                if (e.response) {
                    const { data } = e.response;
                    console.error(data);
                }
            }
    }
    const responseKaKao= (res) => {

        console.log(res);
        console.log(res.response.access_token);
        const { data } = this.state;

        this.setState({
          data: res,
        });
    
        fetch(`/user/signin/kakao`, {
          //백엔드에서 원하는 형태의 endpoint로 입력해서 fetch한다. 
          method: 'GET',
          headers: {
            Authorization: res.response.access_token,
            //받아오는 response객체의 access_token을 통해 유저 정보를 authorize한다. 
           
          },
        })
          .then((res) => res.json())
          .then((res) => localStorage.setItem('token', res.token), 
                //백엔드에서 요구하는 key 값(token)으로 저장해서 localStorage에 저장한다.
                //여기서 중요한것은 처음에 console.log(res)해서 들어오는 
                //access_token 값을 백엔드에 전달해줘서 백엔드에 저장 해두는 
                //절차가 있으므로 까먹지 말 것! 
                alert('로그인 성공하였습니다'));
    }
   

    const onSubmit = async (e) => {
        e.preventDefault();
        // formdata 정리
        let formData = new FormData(); 
        formData.append('username', e.target.username.value); 
        formData.append('password',e.target.password.value);

       // console.log("form data 확인",e.target.username.value);
        //console.log("form data 확인",e.target.password.value);
        // axios로 통신
        try {
            const response = await axios.post('/accounts/login/', formData);
           // console.log(response);
            // 로그인 완료 후 처리
            const {
                data: { token: jwtToken,
                        user: currentUserName }
            } = response;
            
            const {
                id : idT
            } = currentUserName;
            //console.log('response 확인 ', currentUserName);
            dispatch(setToekn(jwtToken));
            dispatch(setId(idT));
            history.push('/');
        } catch (e) {
            setShow(true);
            if (e.response) {
                const { data } = e.response;
                console.error(data);
            }
        }
    };
    
    // eslint-disable-next-line no-undef
    
    
    
    return (

        <div className="container-fluid">
        <div className="bg">
            <Form className="form-login" onSubmit={onSubmit}>
                <Form.Group controlId="username">
                <Form.Control type="text" placeholder="아이디"  />
                </Form.Group>

                <Form.Group controlId="password">
                <Form.Control type="password" placeholder="비밀번호"  />
                </Form.Group>
                {show ? (<Alert variant="danger">비밀번호를 확인하세요.</Alert>) : (<> </>)}
                
                <Button variant="outline-dark" type="submit">
                로그인
                </Button>
                <hr />
                <KaKaoBtn
                jsKey={'13fdd47dd448192e3f9b6d35e6960217'}
                buttonText='카카오 계정으로 로그인'
                onSuccess={responseKaKao}
                getProfile={true}
              />
                <Button variant="success" onClick={loginWithNaver}>
                네이버 로그인
                </Button>
                <br /> <br /> 
                <Link to="/accounts/UserJoin" >회원가입 /</Link> <Link to="" >비밀번호 찾기</Link>
            </Form>
        </div>    
        </div>
        
    );
};
export default SignIn;
