import axios from 'axios';
import React, {useState} from 'react';
import {useHistory} from 'react-router-dom';
import { setToekn, setId } from 'store';
import { useAppContext } from 'store';

import kakobtn from '../../images/kakao_login_medium_wide.png';
import naverbtn from '../../images/naver_Green.PNG';
import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';

const SignIn = () => {
    const {store, dispatch} = useAppContext();
    const history = useHistory();
    const [show, setShow] = useState(false);

    const onSubmit = async (e) => {
        e.preventDefault();
        // formdata 정리
        let formData = new FormData(); 
        formData.append('username', e.target.username.value); 
        formData.append('password',e.target.password.value);

       // console.log("form data 확인",e.target.username.value);
        //console.log("form data 확인",e.target.password.value);
        // axios로 통신
        try {
            const response = await axios.post('/accounts/login/', formData);
           // console.log(response);
            // 로그인 완료 후 처리
            const {
                data: { token: jwtToken,
                        user: currentUserName }
            } = response;
            
            const {
                id : idT
            } = currentUserName;
            //console.log('response 확인 ', currentUserName);
            dispatch(setToekn(jwtToken));
            dispatch(setId(idT));
            history.push('/');
        } catch (e) {
            setShow(true);
            if (e.response) {
                const { data } = e.response;
                console.error(data);
            }
        }
    };
    
    
    
    return (
        <div className="container">
            <div className="background login">
     
            <div className="account-title" id="sign_in_title">로그인</div>
      
            <input type="hidden" id="type" />
            <input type="hidden" id="redirect_referer" value="" /> 
      

            <input type="text" class="input account sign-in" id="email" maxlength="11" placeholder="휴대폰 번호" oninput="this.value = this.value.replace(/[^0-9.]/g, '').replace(/(\..*)\./g, '$1');" />
            <input type="password" class="input account sign-in" id="password" maxlength="64" placeholder="비밀번호" autocomplete="on" />
            <div class="remember-me-checkbox">
                <label class="checkbox"><input type="checkbox" id="remember_me" value="1" />아이디 저장하기
                </label>
            </div>
      
            <div class="button account sign-in" id="sign_in_submit">로그인</div>
      
            <div class="divider-1 sign-in"></div>
      
            <div class="sign-up-button-wrapper">
            <div class="button account parent" id="parent_sign_up">회원가입</div>
            </div>
      
            <div class="go-to-reset-password" id="go_to_reset_password">비밀번호를 잊으셨나요?</div>

            </div>
        </div>    
       
        
    );
};
export default SignIn;
