import React, { Component } from 'react';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';

class FindSitter extends Component {
    getSomeItem = () => {
        axios
        .get('/shop/'+this.props.match.params.category_slug+'/?page='+this.state.page)
        .then(({ data }) => {
            console.log(data);
            
            this.setState({...this.state, products:data[0].products})
        })
        .catch((e) => {
            console.error(e);
        });
    };

    render() {
        return (
        <div>
            <InputGroup>
                <InputGroup.Prepend>
                    <InputGroup.Text id="basic-addon1">제목</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl
                    placeholder=""
                    aria-label="Username"
                    aria-describedby="basic-addon1"
                />
            </InputGroup>
            <InputGroup>
                <InputGroup.Prepend>
                    <InputGroup.Text>상세 요구사항</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl as="textarea" aria-label="With textarea" />
            </InputGroup>
            <Button variant="info" onClick={onSubmit}>저장하기</Button>
        </div>
        );
    }
}

export default FindSitter;
