import axios from 'axios';
import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import {useHistory} from 'react-router-dom';
import { setToekn } from 'store';
import { useAppContext } from 'store';
import KaKaoBtn from 'react-kakao-login';
import icon from '../../images/icon.PNG';
import kakabtn from '../../images/kakao_login_medium_narrow.png';
import { Link } from 'react-router-dom';

const SignIn = () => {
    const {store, dispatch} = useAppContext();
    const history = useHistory();
    const { Kakao } = window;
    const { naver } = window;

    const responseKaKao = (res) => {
        console.log("#####" + res.response.key);
        console.log("#####" + res.token);
        console.log("#####" + res.response.access_token);
        const USER_ACCESS_TOKEN = res.response.access_token;
        Kakao.Auth.setAccessToken(USER_ACCESS_TOKEN);

        //TODO: 처음 사용자이면 사용자 정보를 장고에 보내서 저장
        // 기 등록된 회원이면 로그인 했다고 알림
        Kakao.API.request({
            url: '/v2/user/me',
            success: function(response) {
                console.log("success:" + response.nickName);
                console.log("success:" + response.profileImageUrl);
                console.log("success:" + response.thumbnailUrl);
                console.log("success:" + response.countryISO);
            },
            fail: function(error) {
                console.log(error);
            }
        });

        dispatch(setToekn(USER_ACCESS_TOKEN));
        history.push('/');
      };

    const naverLogin = () => {
        const naverLogin = new naver.LoginWithNaverId({
            clientId: "JT8MZMpN9i8c6zKvpvZL",
            callbackUrl: "http://localhost:3000/",
            isPopup: false,
            loginButton: {color: "green", type: 3, height: 60} ,
            callbackHandle: true
          });
          naverLogin.init();

          window.addEventListener('load', function () {
            naverLogin.getLoginStatus(function (status) {

                console.log("#######" +status);

                if (status) {
                    var email = naverLogin.user.getEmail();
                    var name = naverLogin.user.getNickName();
                    var profileImage = naverLogin.user.getProfileImage();
                    var birthday = naverLogin.user.getBirthday();			
                    var uniqId = naverLogin.user.getId();
                    var age = naverLogin.user.getAge();
                    console.log(email);
                    console.log(name);
                    console.log(profileImage);
                    console.log(birthday);
                    console.log(uniqId);
                    console.log(age);
                } else {
                    console.log("AccessToken이 올바르지 않습니다.");
                }
            });
          });

          window.location.href.includes('access_token') && GetUser();
          function GetUser() {
            const location = window.location.href.split('=')[1];
            const token = location.split('&')[0];
            console.log("token: ", token);
            dispatch(setToekn(token));
            history.push('/');
            //localStorage.setItem("access_token", res.token);
          }
    }

    const [ show, setShow ] = useState(false);

    const onSubmit = async (e) => {
        e.preventDefault();
        // formdata 정리
        const formData = new FormData(e.target);
        console.log("form data 확인",JSON.stringify(formData));
        // axios로 통신
        try {
            const response = await axios.post('/accounts/login/', formData);
            // 로그인 완료 후 처리
            const {
                data: { token: jwtToken },
            } = response;
            console.log('토큰 확인 ', jwtToken);
            dispatch(setToekn(jwtToken));
            history.push('/');
        } catch (e) {
            if (e.response) {
                setShow(true);
                const { data } = e.response;
                console.error(data);
            }
        }
    };



    return (
        <div>
        <div className="container">
            <form action="/action_page.php">
                 <div className="row ">

                        <div className="vl">
                            <span className="vl-innertext">or</span>
                        </div>

                        <div className="col">
                            <Link to="/" alt="" className="fb btn">
                                <img src={kakabtn} alt=""/>
                            </Link>
                            <a href="/" alt="" className="twitter btn">
                                <i className="fa fa-twitter fa-fw"></i> Naver 로그인
                            </a>
                            <a href="/" alt="" className="google btn">
                                <i className="fa fa-google fa-fw"></i> Google+ 로그인
                            </a>
                        </div>

                        <div className="col">

                            <input type="text" className="username" placeholder="전화번호" required />
                            <input type="password" className="password" placeholder="비밀번호" required />
                            <input type="submit" value="Login" />
                        </div>

                    </div>
                </form>
            </div>

            <div className="bottom-container">
                <div className="row">
                    <div className="col">
                        <a href="/"  className="btn text-white">회원 가입</a>
                    </div>
                    <div class="col">
                        <a href="/"  className="btn text-white">비밀번호 찾기</a>
                    </div>
                </div>
            </div>
      </div>
    );
};
export default SignIn;
