import axios from 'axios';
import React, {useState} from 'react';
import {useHistory} from 'react-router-dom';
import { setToekn, setId } from 'store';
import { useAppContext } from 'store';

import kakobtn from '../../images/kakao_login_medium_wide.png';
import naverbtn from '../../images/naver_Green.PNG';
import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';

const SignIn = () => {
    const {store, dispatch} = useAppContext();
    const history = useHistory();
    const [show, setShow] = useState(false);

    const onSubmit = async (e) => {
        e.preventDefault();
        // formdata 정리
        let formData = new FormData(); 
        formData.append('username', e.target.username.value); 
        formData.append('password',e.target.password.value);

       // console.log("form data 확인",e.target.username.value);
        //console.log("form data 확인",e.target.password.value);
        // axios로 통신
        try {
            const response = await axios.post('/accounts/login/', formData);
           // console.log(response);
            // 로그인 완료 후 처리
            const {
                data: { token: jwtToken,
                        user: currentUserName }
            } = response;
            
            const {
                id : idT
            } = currentUserName;
            //console.log('response 확인 ', currentUserName);
            dispatch(setToekn(jwtToken));
            dispatch(setId(idT));
            history.push('/');
        } catch (e) {
            setShow(true);
            if (e.response) {
                const { data } = e.response;
                console.error(data);
            }
        }
    };
    
    
    
    return (
        <div className="container">
            <div className="background login">
     
            <div className="login-title" >로그인</div>

            <input type="text" className="input " placeholder="휴대폰 번호"/>
            <input type="password" className="input " placeholder="비밀번호" autocomplete="on" />
            <div className="remember-me-checkbox">
                <label className="checkbox">
                    <input type="checkbox" id="remember_me" value="1" />아이디 저장하기
                </label>
            </div>
      
            <button>로그인</button>
      
            <div className="divider-1 sign-in"></div>
      
            <div className="sign-up-button-wrapper">
            <button>회원가입</button>
            </div>
      
            <button>비밀번호 찾기</button>

            </div>
        </div>    
       
        
    );
};
export default SignIn;
