import axios from 'axios';
import React, {useState} from 'react';
import {useHistory} from 'react-router-dom';
import { setToekn, setId } from 'store';
import { useAppContext } from 'store';

import kakobtn from '../../images/kakao_login_medium_wide.png';
import naverbtn from '../../images/naver_Green.PNG';
import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';

const SignIn = () => {
    const {store, dispatch} = useAppContext();
    const history = useHistory();
    const [show, setShow] = useState(false);

    const onSubmit = async (e) => {
        e.preventDefault();
        // formdata 정리
        let formData = new FormData(); 
        formData.append('username', e.target.username.value); 
        formData.append('password',e.target.password.value);

       // console.log("form data 확인",e.target.username.value);
        //console.log("form data 확인",e.target.password.value);
        // axios로 통신
        try {
            const response = await axios.post('/accounts/login/', formData);
           // console.log(response);
            // 로그인 완료 후 처리
            const {
                data: { token: jwtToken,
                        user: currentUserName }
            } = response;
            
            const {
                id : idT
            } = currentUserName;
            //console.log('response 확인 ', currentUserName);
            dispatch(setToekn(jwtToken));
            dispatch(setId(idT));
            history.push('/');
        } catch (e) {
            setShow(true);
            if (e.response) {
                const { data } = e.response;
                console.error(data);
            }
        }
    };
    
    
    
    return (
        <div className="background account sign-in">
        <div className="container">
        <form onSubmit={onSubmit}>
            <div className="row ">
                <div className="col">
                    <Link to="/" alt="" >
                        <img src={kakobtn} alt=""/>
                    </Link>
                </div>
                        
                <div className="col">
                    <input type="text" className="username" name="username" placeholder="전화번호를 입력하세요" required />
                    <input type="password" className="password" name="password" placeholder="비밀번호를 입력하세요" required />
                    <Button type="submit">로그인</Button>
                    {show ? (<Alert variant="danger">아이디/비밀번호를 확인하세요.</Alert>) : (<br/>)}
                    
                </div>
            </div>
            </form>    
        </div>
      
        <div className="bottom-container">
            <div className="row">
                <div className="col">
                    <Link to="/accounts/UserJoin" className="btn text-white">회원 가입 </Link>
                </div>
                <div class="col">
                    <Link to="/" className="btn text-white">비밀번호 찾기 </Link>
                </div>
            </div>
        </div>
        </div>
    );
};
export default SignIn;
