import React , { useState } from 'react';
import { Link } from 'react-router-dom';

import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';

import KakaoMap from 'components/KakaoMap';
import GetSitterJobs from 'components/GetSitterJobs';

const FindSitter = () => {
    const [mapView, setMapView] = useState(true);
    const changeViewType = (value) => ({
        //setMapView(value);
    });
    return (
        <div>
        <Link className="mr-auto" to="/Jobs/FindSitter">
            <span> 시터 찾기 ></span> 
        </Link>
            <Container>
                    <h1>내 집 주변 시터 일자리</h1>
                    <p>
                        회원가입과 안심인증을 한 뒤에는 서비스를 무료로 이용 할 수 있습니다.
                        집 주변의 시터 구인 구직 정보를 한꺼번에 볼 수 있습니다.
                        원하는 일자리나 시터에게 메세지를 보내 보세요~
                    </p>

                    <Link className="mr-auto" to="/Jobs/RegisterSitterJobs">
                        시터 일자리 등록
                    </Link>
            </Container>
            { mapView ?
                (
                    <Button  onClick={changeViewType(false)}>목록으로 보기</Button>
                ) :
                (
                    <Button  onClick={changeViewType(true)}>지도로 보기</Button>
                )
            
            } 
            { mapView ?
                (
                   <KakaoMap/> 
                ) :
                (
                   <GetSitterJobs/>
                )
            
            } 
            
            
                
        
        </div>
    );

}

export default FindSitter;
