import axios from 'axios';
import React, {useState} from 'react';
import {useHistory} from 'react-router-dom';
import { setToekn, setId } from 'store';
import { useAppContext } from 'store';

import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

const SignIn = () => {
    const {dispatch} = useAppContext();
    const history = useHistory();
    const [show, setShow] = useState(false);

    const onSubmit = async (e) => {
        e.preventDefault();
        // formdata 정리
        let formData = new FormData(); 
        formData.append('username', e.target.username.value); 
        formData.append('password',e.target.password.value);

       // console.log("form data 확인",e.target.username.value);
        //console.log("form data 확인",e.target.password.value);
        // axios로 통신
        try {
            const response = await axios.post('/accounts/login/', formData);
           // console.log(response);
            // 로그인 완료 후 처리
            const {
                data: { token: jwtToken,
                        user: currentUserName }
            } = response;
            
            const {
                id : idT
            } = currentUserName;
            //console.log('response 확인 ', currentUserName);
            dispatch(setToekn(jwtToken));
            dispatch(setId(idT));
            history.push('/');
        } catch (e) {
            setShow(true);
            if (e.response) {
                const { data } = e.response;
                console.error(data);
            }
        }
    };
    
    
    
    return (
        <div className="container-login">
            <Form className="form-login" onSubmit={onSubmit}>
            <Form.Group controlId="formBasicEmail">

                <Form.Control type="text" placeholder="전화번호" />
                <Form.Text className="text-muted">
                    전화번호는 공개하지 않습니다.
                </Form.Text>
                </Form.Group>

                <Form.Group controlId="formBasicPassword">
                <Form.Control type="password" placeholder="비밀번호" />
            </Form.Group>
      
            <Button variant="outline-dark" type="submit">
                로그인
            </Button>
            <hr />
            <Button variant="warning" type="submit">
                카카오 로그인
            </Button>
            <Button variant="success" type="submit">
                네이버 로그인
            </Button>
            <hr />
            <Link to="" >회원가입</Link>
        </Form>

        
        </div>    
       
        
    );
};
export default SignIn;
