import axios from 'axios';

import {useHistory} from 'react-router-dom';

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
//import Alert from 'react-bootstrap/Alert';

const UserCreate = () => {
    const history = useHistory();
    // 회원 가입 API 호출
    const userJoin = async (e) => {
        // 1. 기본 동작 막기
        e.preventDefault();
        // 2. 데이터 준비하기
        const formData = new FormData(e.target);
        // 3. api 호출
        await axios
            .post('/accounts/join/', formData)
            .then(({ data }) => {
                console.log(data);
                history.push('/');
            })
            .catch((e) => {
                console.error(e);
                alert(e);
            });
    };
    return (
        <div className="container" >

            <Form className="form-join" onSubmit={userJoin}>

               <div className="brick-join">
                    <p className="join-title">회원가입</p>

                    <p class="form-title">로그인 정보</p>
                    <Form.Group controlId="username">
                        <Form.Control type="text" placeholder="아이디는 전화번호로 입력해주세요~"  />
                    </Form.Group>
                    <Form inline>
                    <Form.Group>
                        <Form.Label htmlFor="inputPassword6">비밀번호&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</Form.Label>
                            <Form.Control type="password" className="mx auto" id="inputPassword6" aria-describedby="passwordHelpInline"/>
                            <Form.Text id="passwordHelpBlock" muted>
                            &nbsp;Must be 8-20 characters long.
                            </Form.Text>
                    </Form.Group>
                    </Form>
                    <br />
                    <Form inline>
                    <Form.Group>
                        <Form.Label htmlFor="inputPassword6">비밀번호 확인 &nbsp;</Form.Label>
                            <Form.Control type="password" className="mx auto" id="inputPassword6" aria-describedby="passwordHelpInline"/>
                    </Form.Group>
                    </Form>
                    
                

                    <p className="warning-sign" id="password_msg" style={{display:"none"}}></p>
                    <br/>
                    <hr /> 
                    <p class="form-title">회원 정보</p>
                    <Form.Group controlId="nickname">
                        <Form.Control type="text" placeholder="실명을 입력해주세요."  />
                    </Form.Group>
                    <Form.Group  md="4" controlId="email">
                        <Form.Control type="email" placeholder="이메일 주소를 입력해주세요."  />
                    </Form.Group>
                    <Button variant="outline-dark" type="submit">
                    회원가입
                    </Button>
               </div>
                
                
                
                
            </Form>
        </div>
        
    );
};

export default UserCreate;
