import axios from 'axios';

import {useHistory} from 'react-router-dom';

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
//import Alert from 'react-bootstrap/Alert';

const UserCreate = () => {
    const history = useHistory();
    // 회원 가입 API 호출
    const userJoin = async (e) => {
        // 1. 기본 동작 막기
        e.preventDefault();
        // 2. 데이터 준비하기
        const formData = new FormData(e.target);
        // 3. api 호출
        await axios
            .post('/accounts/join/', formData)
            .then(({ data }) => {
                console.log(data);
                history.push('/');
            })
            .catch((e) => {
                console.error(e);
                alert(e);
            });
    };
    return (
        <div className="container-small" >

            <Form className="form-join" onSubmit={userJoin}>

               <div className="brick-join">
                    <p className="join-title">회원가입</p>

                    <p class="form-title">로그인 정보</p>
                    <Form.Group controlId="username">
                        <Form.Control type="text" placeholder="아이디는 전화번호로 입력해주세요~"  />
                    </Form.Group>
                    <Form.Group controlId="password">
                        <Form.Control type="password" placeholder="비밀번호를 입력해 주세요. (8~16자/ 문자, 숫자, 특수문자 혼용)" onkeyup="valid_check_pwd(1)" autoComplete="off"/>
                    </Form.Group>
                
                    <Form.Group controlId="re-password">
                        <Form.Control type="password" placeholder="비밀번호를 다시 입력해주세요."  />
                    </Form.Group>
                    <p className="warning-sign" id="password_msg" style={{display:"none"}}></p>
                    <br/>
                    <hr /> 
                    <p className="form-title">회원 정보</p>
                    <Form.Group controlId="nickname">
                        <Form.Control type="text" placeholder="실명을 입력해주세요."  />
                    </Form.Group>
                    <Form.Group  md="4" controlId="email">
                        <Form.Control type="email" placeholder="이메일 주소를 입력해주세요."  />
                    </Form.Group>
                    <Form inline>
                    <input type="text" title="우편번호 통합" style={{width:"50px"}} readOnly />
                    <Button variant="primary" className="mb-2">우편번호 찾기</Button>
                    </Form>
                    
            
                    <Button variant="outline-dark" type="submit">
                    회원가입
                    </Button>
               </div>
                
                
                
                
            </Form>
        </div>
        
    );
};

export default UserCreate;
