/* daum */
/* global Kakao */
import React, {useState, useEffect} from 'react';
import axios from 'axios';

import {useHistory} from 'react-router-dom';

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';

const UserCreate = () => {

    const [address, setAddressr] = useState();

    useEffect(() => {
        //const script = document.createElement('script')
        //script.src = 'https://developers.kakao.com/sdk/js/kakao.js'
        //script.async = true
    
        if(!Kakao.isInitialized()) {
            Kakao.init('13fdd47dd448192e3f9b6d35e6960217');
        }
        // SDK 초기화 여부를 판단합니다.
        console.log(Kakao.isInitialized());

      return () => {
      };
      
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const getZipCode = () => {
    // eslint-disable-next-line no-undef
        new daum.Postcode({
            oncomplete: function(data) {
            // 팝업에서 검색결과 항목을 클릭했을때 실행할 코드를 작성하는 부분입니다.
            // 예제를 참고하여 다양한 활용법을 확인해 보세요.
                setAddressr(data.address);
                console.log(data);
            }
        }).open();

    };

    const loginWithKakao= async (e) => {
        try{
            if(!Kakao) {
                console.log("no kakao");
            }
            Kakao.Auth.login({
                success:(auth) => {
                    console.log("login success! " + auth);
                    //console.log( auth);
                    if (!Kakao.Auth.getAccessToken()) {
                        Kakao.Auth.setAccessToken(auth);
                    }
                    
                    if (Kakao.Auth.getAccessToken()) {
                        console.log('logged in.');
                        Kakao.API.request({
                            url: '/v2/user/me',
                            success: function(response) {
                                var sPerson = JSON.stringify(response);
                                var oPerson = JSON.parse(sPerson);
                                console.log(oPerson.kakao_account.profile.nickname);
                                console.log(oPerson.kakao_account.profile.profile_image_url);
                            },
                            fail: function(error) {
                                console.log(error);
                            }
                        });
                        
                    }
                },
                fail: (err) => {
                    console.error(err);
                }
            })
        } catch(err) {
            console.log(err);
        }
    };

    const inputLoginInfoSelf = () => {
        var loginForm = document.getElementById("selfLogin");
        loginForm.style = "display:block";
    };

    const history = useHistory();
    // 회원 가입 API 호출
    const userJoin = async (e) => {
        // 1. 기본 동작 막기
        e.preventDefault();
        // 2. 데이터 준비하기
        const formData = new FormData(e.target);
        // 3. api 호출
        await axios
            .post('/accounts/join/', formData)
            .then(({ data }) => {
                console.log(data);
                history.push('/');
            })
            .catch((e) => {
                console.error(e);
                alert(e);
            });
    };
    return (
        <Container fluid="md">
     
            <Form className="form-join" onSubmit={userJoin}>

               <div className="brick-join">
                    <p className="join-title">회원가입</p>

                    <p className="form-title">로그인 정보</p>
                    <Form.Row className="justify-content-md-center">
                    <Col  md="auto" >
                        <Button variant="warning" onClick={loginWithKakao}>카카오 로그인 정보 입력</Button>
                    </Col>
                    <Col  md="auto">
                        <Button variant="success">네이버 로그인 정보 입력</Button>
                    </Col>
                    <Col  md="auto">
                        <Button variant="danger">&nbsp;구글 로그인 정보 입력 &nbsp;</Button>
                    </Col>
                    <Col  md="auto">
                        <Button variant="dark" onClick={inputLoginInfoSelf}>&nbsp;&nbsp;로그인 정보 직접입력 &nbsp;&nbsp;</Button>
                    </Col>
                    </Form.Row>
                    <br />
                    <div id="selfLogin" style={{display:"none"}}>
                    <Form.Row className="justify-content-md-center">
                    <Col >
                        <Form.Control type="text" placeholder="아이디는 전화번호로 입력해주세요~"  />
                    </Col> 
                    </Form.Row >

                    <Form.Row className="justify-content-md-center">
                    <Col >
                        <Form.Control type="password" placeholder="비밀번호를 입력해 주세요. (8~16자/ 문자, 숫자, 특수문자 혼용)" autoComplete="off"/>
                    </Col> 
                    </Form.Row>

                    <Form.Row className="justify-content-md-center">
                    <Col>
                        <Form.Control type="password" placeholder="비밀번호를 다시 입력해주세요."  />
                    </Col> 
                    </Form.Row>
                    </div>

                    <Form.Row className="justify-content-md-center">
                    <p className="warning-sign" id="password_msg" style={{display:"none"}}></p>
                    </Form.Row>
                    <br/>
                    <hr /> 
                    <p className="form-title">회원 정보</p>
                    <Form.Row className="justify-content-md-center">
                    <Col xs={12}>
                        <Form.Control type="text" placeholder="실명을 입력해주세요."  />
                    </Col> 
                    </Form.Row>
                    <Form.Row className="justify-content-md-center">
                    <Col>
                        <Form.Control type="text" placeholder="휴대전화번호를 입력해주세요."  />
                    </Col>
                    <Col md="auto">
                    <Button variant="outline-primary">휴대폰 인증</Button>
                    </Col>
                    </Form.Row>
                    <Form.Row className="justify-content-md-center">
                    <Col >
                    <Form.Control type="email" placeholder="이메일 주소를 입력해주세요."  />
                    </Col>
                    <Col md="auto">
                    <Button variant="outline-primary">이메일 인증</Button>
                    </Col>
                    </Form.Row>

                    <Form.Row className="justify-content-md-center">
                    <Col md="auto">
                    <Form.Control className="mb-2 mr-sm-2" placeholder="우편번호 찾기"  onClick={getZipCode} readOnly />
                    </Col>
                    <Col>
                    <Form.Control className="mb-2 mr-sm-2"  type="text" onClick={getZipCode} placeholder="주소를 선택해주세요." id="address1" value={address} readOnly />
                    </Col>
                    </Form.Row>    

                    <Form.Row className="justify-content-md-center">
                    <Col xs={12}>
                    <Form.Control className="mb-2 mr-sm-2"  type="text" placeholder="나머지 주소를 입력주세요." id="address2" />
                    </Col>
                    </Form.Row>   
            
                    <Button variant="outline-dark" type="submit">
                    회원가입
                    </Button>
               </div>
                
                
                
                
            </Form>
        </Container>
        
    );
};

export default UserCreate;
