import axios from 'axios';
import {useHistory} from 'react-router-dom';
import { setToekn } from 'store';
import { useAppContext } from 'store';




const SignIn = () => {

    const { naver } = window;

    const Login = () => {
        const naverLogin = new naver.LoginWithNaverId({
            clientId: "JT8MZMpN9i8c6zKvpvZL",
            callbackUrl: "http://localhost:3000/",
            isPopup: false,
            loginButton: {color: "green", type: 3, height: 60} ,
            callbackHandle: true
          });
          naverLogin.init();

          window.addEventListener('load', function () {
            naverLogin.getLoginStatus(function (status) {
                if (status) {
                    var email = naverLogin.user.getEmail();
                    var name = naverLogin.user.getNickName();
                    var profileImage = naverLogin.user.getProfileImage();
                    var birthday = naverLogin.user.getBirthday();			
                    var uniqId = naverLogin.user.getId();
                    var age = naverLogin.user.getAge();
                    console.log(email);
                    console.log(name);
                    console.log(profileImage);
                    console.log(birthday);
                    console.log(uniqId);
                    console.log(age);
                } else {
                    console.log("AccessToken이 올바르지 않습니다.");
                }
            });
          });


          window.location.href.includes('access_token') && GetUser();
          function GetUser() {
            const location = window.location.href.split('=')[1];
            const token = location.split('&')[0];
            console.log("token: ", token);

            //localStorage.setItem("access_token", res.token);
          }
    }

    const {store, dispatch} = useAppContext();
    const history = useHistory();
    const onSubmit = async (e) => {
        e.preventDefault();
        // formdata 정리
        const formData = new FormData(e.target);
        console.log("form data 확인",JSON.stringify(formData));
        // axios로 통신
        try {
            const response = await axios.post('/accounts/token/', formData);
            // 로그인 완료 후 처리
            const {
                data: { token: jwtToken },
            } = response;
            console.log('토큰 확인 ', jwtToken);
            dispatch(setToekn(jwtToken));
            history.push('/');
        } catch (e) {
            if (e.response) {
                const { data } = e.response;
                console.error(data);
            }
        }
    };
    return (
        <div className="container">
            <form onSubmit={onSubmit}>
            <div class="col-md-4 col-md-offset-4">
            <div class="panel panel-default">
                <div class="panel-heading">
                    로그인
                </div>
                <div class="panel-body">
                    <input type="text" class="form-control" placeholder="Email" />
                    <br />
                    <input type="text" class="form-control" placeholder="비밀번호" />
                    <br />
                    <div id="naverIdLogin" onClick={Login}>
                        <a href="#" class="btn btn-lg btn-block">
                            <img src="https://static.nid.naver.com/oauth/big_g.PNG"  alt=""  width="320" />
                        </a>
                    </div>

                    <br />

                    <a href="#" class="btn btn-lg btn-primary btn-block">
                        <i class="fa fa-facebook" aria-hidden="true"></i> 페이스북 로그인
                    </a>
                </div>
            </div>
            </div>
            </form>
        </div>
    );
};
export default SignIn;
