import axios from 'axios';

import {useHistory} from 'react-router-dom';

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
//import Alert from 'react-bootstrap/Alert';

const UserCreate = () => {
    const history = useHistory();
    // 회원 가입 API 호출
    const userJoin = async (e) => {
        // 1. 기본 동작 막기
        e.preventDefault();
        // 2. 데이터 준비하기
        const formData = new FormData(e.target);
        // 3. api 호출
        await axios
            .post('/accounts/join/', formData)
            .then(({ data }) => {
                console.log(data);
                history.push('/');
            })
            .catch((e) => {
                console.error(e);
                alert(e);
            });
    };
    return (
        <div className="container" >

            <Form className="form-join" onSubmit={userJoin}>

               <div className="brick-join">
                    <p className="join-title">회원가입</p>

                    <p class="form-title">로그인 정보</p>
                    <Form.Group controlId="username">
                        <Form.Control type="text" placeholder="아이디는 전화번호로 입력해주세요~"  />
                    </Form.Group>
                    <Form.Group controlId="password">
                        <Form.Control type="password" placeholder="비밀번호를 입력해 주세요. (8~16자/ 문자, 숫자, 특수문자 혼용)" onkeyup="valid_check_pwd(1)" autocomplete="off"/>
                    </Form.Group>
                
                    <Form.Group controlId="re-password">
                        <Form.Control type="password" placeholder="비밀번호를 다시 입력해주세요."  />
                    </Form.Group>
                    <p class="warning-sign" id="valid_check_msg_pwd" style="display:none;"></p>
                    <Button variant="outline-dark" type="submit">
                    회원가입
                    </Button>
               </div>
                
                
                
                
            </Form>
        </div>
        
    );
};

export default UserCreate;
