import React from 'react';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';

import axios from 'axios';
import { setToken } from 'store';
import { useAppContext } from 'store';
import { useHistory } from 'react-router-dom';

const FindSitter = () => {
    const { store, dispatch } = useAppContext();
    const history = useHistory();
    const sendJobData = async (e) => {
        e.preventDefault();
        // formdata 정리
        const formData = new FormData(e.target);
        console.log('form data 확인', JSON.stringify(formData));
        // axios로 통신
        try {
            const response = await axios.post('/accounts/token/', formData);
            // 로그인 완료 후 처리
            const {
                data: { token: jwtToken },
            } = response;
            console.log('토큰 확인 ', jwtToken);
            // 토큰 저장!!
            dispatch(setToken(jwtToken));
            history.push('/');
        } catch (e) {
            if (e.response) {
                const { data } = e.response;
                console.error(data);
            }
        }
    };

    

    return (
        <div className="container">
        <form onSubmit={this.sendJobData}>
            <div className="form-group">
                <label>제목</label>
                <input type="text" className="form-control" name="title" />
            </div>
            <div className="form-group">
                <label>요구사항</label>
                <textarea className="form-control"
                    name="contents"></textarea>
            </div>

            <button type="submit" className="btn btn-primary">
                저장하기
            </button>
        </form>
    </div>
    );

}

export default FindSitter;
