import React, {useEffect, useState} from 'react';
import {useHistory} from 'react-router-dom';

import axios from 'axios';

import {getStorageItem} from 'utils/sessionstorage';

const WriteSitterJobs = () => {
    const history = useHistory();
    const [author, setAuthor] = useState('initial');
    const AUTH_TOKEN = 'Token ' + getStorageItem('jwtToken');

    useEffect(() => {
        const id = getStorageItem('id');
        axios.defaults.headers.common['Authorization'] = AUTH_TOKEN;
        const response = axios.get('/accounts/userdetail/' + id);
        console.log('response 확인 ', response);
        const {
            data: { 
                    username: author }
        } = response;
        setAuthor();
        console.log("author2:" + author);

        return () => {
        };
      }, [author]);

    const sendJobData = async (e) => {
        e.preventDefault();
        // formdata 정리
       
        console.log(author);
        const formData = new FormData(e.target);
        //console.log('form data 확인', JSON.stringify(formData));
        //console.log(getStorageItem('jwtToken'));
        
        // axios로 통신
        try {
            axios.defaults.headers.common['Authorization'] = AUTH_TOKEN;
            const response = await (await axios.post('/api/sitterJobs/', formData));
            console.log('response 확인 ', JSON.stringify(response));
           
            history.push('/');
        } catch (e) {
            if (e.response) {
                const { data } = e.response;
                console.error(data);
            }
        }
    };

    return (
        <div className="container">
        <form onSubmit={sendJobData}>
            <div className="form-group">
                <input type="hidden" className="form-control" name="author" value={author} />
                <label>제목</label>
                <input type="text" className="form-control" name="title" />
                <label>자녀 수</label>
                <input type="number" className="form-control" name="numbers" />
                <label>요구사항</label>
                <textarea className="form-control"
                    name="contents"></textarea>
            </div>

            <button type="submit" className="btn btn-primary">
                저장하기
            </button>
        </form>
    </div>
    );

}

export default WriteSitterJobs;
