import React, { Component } from 'react';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';

import axios from 'axios';
import { setToken } from 'store';
import { useAppContext } from 'store';

class FindSitter extends Component {
   // const { store, dispatch } = useAppContext();
    sendJobData(e) {
       e.preventDefault();

       const formData = new new FormData(e.target);
       console.log('form data 확인', JSON.stringify(formData));

       try {
        const response = axios.post('/accounts/token/', formData);
        // 로그인 완료 후 처리
        const {
            data: { token: jwtToken },
        } = response;
        console.log('토큰 확인 ', jwtToken);
        // 토큰 저장!!
            //dispatch(setToken(jwtToken));
            //history.push('/');
        } catch (e) {
            if (e.response) {
                const { data } = e.response;
                console.error(data);
            }
        }
    }

    componentDidMount() {
        
    }

    render() {
        return (
        <div>
            <InputGroup>
                <InputGroup.Prepend>
                    <InputGroup.Text id="basic-addon1">제목</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl
                    placeholder=""
                    aria-label="Username"
                    aria-describedby="basic-addon1"
                />
            </InputGroup>
            <InputGroup>
                <InputGroup.Prepend>
                    <InputGroup.Text>상세 요구사항</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl as="textarea" aria-label="With textarea" />
            </InputGroup>
            <Button variant="info" onClick={this.sendJobData}>저장하기</Button>
        </div>
        );
    }
}

export default FindSitter;
